CREATE TABLE IF NOT EXISTS `fields` (`id` int(11) NOT NULL auto_increment, `fieldid` varchar(255) NOT NULL, `label` varchar(255) NOT NULL, `type` varchar(20) NOT NULL, `default` longtext NOT NULL, `required` int(1) NOT NULL, `description` longtext NOT NULL, `form` int(11) NOT NULL, `order` int(11) NOT NULL, PRIMARY KEY  (`id`),  KEY `id` (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=18 ;
CREATE TABLE IF NOT EXISTS `forms` (`id` int(11) NOT NULL auto_increment, `name` varchar(255) NOT NULL, `description` longtext NOT NULL, `divid` varchar(255) NOT NULL, `button` varchar(255) NOT NULL, `email` varchar(255) NOT NULL, `order` int(11) NOT NULL, KEY `id` (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=5 ;
CREATE TABLE IF NOT EXISTS `options` (`id` int(11) NOT NULL auto_increment, `value` varchar(255) NOT NULL, `label` varchar(255) NOT NULL, `field` int(11) NOT NULL, `order` int(11) NOT NULL, PRIMARY KEY  (`id`), KEY `id` (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=21 ;